CREATE TABLE IF NOT EXISTS city (id INTEGER primary key AUTOINCREMENT NOT NULL, name TEXT);
CREATE TABLE IF NOT EXISTS user (id INTEGER primary key AUTOINCREMENT, username TEXT NOT NULL, password TEXT NOT NULL, role INTEGER NOT NULL, token TEXT);
CREATE TABLE IF NOT EXISTS neighborhood (id INTEGER primary key AUTOINCREMENT NOT NULL, name TEXT, city_id INTEGER, FOREIGN KEY (city_id) REFERENCES city (id));
CREATE TABLE IF NOT EXISTS plan (id INTEGER primary key AUTOINCREMENT NOT NULL, name TEXT, city_id INTEGER, use_prioritization INTEGER, FOREIGN KEY (city_id) REFERENCES city (id));
CREATE TABLE IF NOT EXISTS questionary (id INTEGER primary key AUTOINCREMENT NOT NULL, name TEXT);
CREATE TABLE IF NOT EXISTS plan_questionary (plan_id INTEGER NOT NULL, questionary_id INTEGER NOT NULL, PRIMARY KEY(plan_id, questionary_id), FOREIGN KEY (plan_id) REFERENCES plan (id), FOREIGN KEY (questionary_id) REFERENCES questionary (id));
CREATE TABLE IF NOT EXISTS app_city_plan (city_id INTEGER, plan_id INTEGER, is_rural_zone BOOLEAN, primary key(city_id, plan_id), FOREIGN KEY (city_id) REFERENCES city (id), FOREIGN KEY (plan_id) REFERENCES plan (id));
CREATE TABLE IF NOT EXISTS question (id INTEGER primary key AUTOINCREMENT NOT NULL, name TEXT, description TEXT, question TEXT, position INTEGER, context_area TEXT, context_area_icon TEXT, is_rural_zone BOOLEAN, metric_id INTEGER);
CREATE TABLE IF NOT EXISTS questionary_question (questionary_id INTEGER NOT NULL, question_id INTEGER NOT NULL, PRIMARY KEY(questionary_id, question_id), FOREIGN KEY (questionary_id) REFERENCES questionary (id), FOREIGN KEY (question_id) REFERENCES question (id));
CREATE TABLE IF NOT EXISTS respondent (id INTEGER primary key AUTOINCREMENT NOT NULL, cpf TEXT, email TEXT, residence_time_range INTEGER, residence_neighborhood_id INTEGER, job_city_id INTEGER, job_neighborhood_id INTEGER, salary_range INTEGER, created_at TEXT, FOREIGN KEY (residence_neighborhood_id) REFERENCES neighborhood (id), FOREIGN KEY (job_city_id) REFERENCES city (id), FOREIGN KEY (job_neighborhood_id) REFERENCES neighborhood (id));
CREATE TABLE IF NOT EXISTS answer (plan_id INTEGER NOT NULL, questionary_id INTEGER NOT NULL, question_id INTEGER NOT NULL, respondent_id INTEGER NOT NULL, answer INTEGER NOT NULL, user_id INTEGER, created_at TEXT, is_completed BOOLEAN, PRIMARY KEY(plan_id, questionary_id, question_id, respondent_id), FOREIGN KEY (plan_id) REFERENCES plan (id), FOREIGN KEY (questionary_id) REFERENCES questionary (id), FOREIGN KEY (question_id) REFERENCES question (id), FOREIGN KEY (respondent_id) REFERENCES respondent (id), FOREIGN KEY (user_id) REFERENCES user (id));
CREATE TABLE IF NOT EXISTS answer_neighborhood (plan_id INTEGER NOT NULL, questionary_id INTEGER NOT NULL, question_id INTEGER NOT NULL, respondent_id INTEGER NOT NULL, neighborhood_id INTEGER NOT NULL, created_at TEXT, PRIMARY KEY(plan_id, questionary_id, question_id, respondent_id, neighborhood_id), FOREIGN KEY (plan_id) REFERENCES plan (id), FOREIGN KEY (neighborhood_id) REFERENCES neighborhood (id), FOREIGN KEY (questionary_id) REFERENCES questionary (id), FOREIGN KEY (question_id) REFERENCES question (id), FOREIGN KEY (respondent_id) REFERENCES respondent (id));
CREATE TABLE IF NOT EXISTS prioritization (plan_id INTEGER NOT NULL, questionary_id INTEGER NOT NULL, question_id INTEGER NOT NULL, respondent_id INTEGER NOT NULL, metric_item INTEGER NOT NULL, metric_value INTEGER NOT NULL, created_at TEXT, PRIMARY KEY(plan_id, questionary_id, question_id, respondent_id, metric_item), FOREIGN KEY (plan_id) REFERENCES plan (id), FOREIGN KEY (questionary_id) REFERENCES questionary (id), FOREIGN KEY (question_id) REFERENCES question (id), FOREIGN KEY (respondent_id) REFERENCES respondent (id));
CREATE TABLE IF NOT EXISTS metric (id INTEGER primary key AUTOINCREMENT, name TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS metric_item (id INTEGER primary key AUTOINCREMENT, name TEXT NOT NULL, metric_id INTEGER, FOREIGN KEY (metric_id) REFERENCES metric (id));
CREATE TABLE IF NOT EXISTS metric_value (id INTEGER primary key AUTOINCREMENT, name TEXT NOT NULL, value INTEGER NOT NULL, icon TEXT NOT NULL, icon_selected TEXT NOT NULL, metric_item_id INTEGER, FOREIGN KEY (metric_item_id) REFERENCES metric_item (id));










